import React, { Component } from 'react'

import {Card, Form, Button, Col} from 'react-bootstrap';

export default class Exercise extends Component {

    
    constructor(props){
        super(props);
        this.state={
            nameEx:'',
            durationEx:'',
            positionEx:'',
            coachEx:''
        };
        this.exerciseChange=this.exerciseChange(this);
        this.submitExercise=this.submitExercise.bind(this);
    }

    submitExercise(event){
        alert(this.state.nameEx);
        event.preventDefault();
    }

    exerciseChange(event){
        this.setState({
            [event.target.name]:event.target.value
        });
    }

    render(){
        return(
            <Card className={"border border-dark bg-dark text-white"}>
                <Card.Header>
                    Add Exercise
                </Card.Header>
                <Form onSubmit={this.submitExercise} id="exerciseFormId"> 
                    <Card.Body>
                        <Form.Row>
                            <Form.Group as={Col} controlId="formGridName">
                                <Form.Label>Exercise Name</Form.Label>
                                <Form.Control required type="text" name="nameEx"
                                value={this.state.nameEx}
                                onChange={this.exerciseChange} 
                                className={"bg-dark text-white"} placeholder="Exercise Name" />
                                <Form.Text className="text-muted">
                                Enter the exercise name.
                                </Form.Text>
                            </Form.Group>
                            <Form.Group as={Col}>
                                <Form.Label>Exercise Duration</Form.Label>
                                <Form.Control required type="text" name="durationEx" className={"bg-dark text-white"} placeholder="Exercise Duration" />
                                <Form.Text className="text-muted">
                                Enter the exercise duration.
                                </Form.Text>
                            </Form.Group>
                        </Form.Row>
                        <Form.Row>
                            <Form.Group as={Col}>
                                <Form.Label>Player Position</Form.Label>
                                <Form.Control type="text" name="positionEx" className={"bg-dark text-white"} placeholder="Player Position" />
                                <Form.Text className="text-muted">
                                Enter the player position for this exercise.
                                </Form.Text>
                            </Form.Group>
                            <Form.Group as={Col}>
                                <Form.Label>Coach Name</Form.Label>
                                <Form.Control type="text" name="coachEx" className={"bg-dark text-white"} placeholder="Coach Name" />
                                <Form.Text className="text-muted">
                                Enter the name of the coach who will lead the exercise.
                                </Form.Text>
                            </Form.Group>
                        </Form.Row>
                    </Card.Body>
                    <Card.Footer style={{"textAlign":"right"}}>
                    <Button variant="success" type="submit">
                            Submit
                        </Button>
                    </Card.Footer>
                </Form>
            </Card>
        );
    }
}
