import React, { Component } from 'react'

import {Card, Form, Button, Col} from 'react-bootstrap';
import {FontAwesomeIcon} from '@fortawesome/react-fontawesome'
import {faPlusSquare, faSave} from '@fortawesome/free-solid-svg-icons'

export default class Exercise extends Component {

    
    constructor(props){
        super(props);
        this.state = this.initialState;
        this.state.show = false;
        this.exerciseChange=this.exerciseChange.bind(this);
        this.submitExercise=this.submitExercise.bind(this);
    }

    // submitExercise(event){
    //     alert(
    //         'Name: '+this.state.nameEx+ 
    //         ', Duration: '+this.state.durationEx+
    //         ', Position: '+this.state.positionEx+
    //         ', Coach: '+this.state.coachEx    
    //     );
    //     event.preventDefault();
    // }

    initialState={
        nameEx:'',
        durationEx:'',
        positionEx:'',
        coachEx:''
    };

    resetExercise = () => {
        this.setState(() => this.initialState);
    }

    submitExercise = event => {
        event.preventDefault();

        const exercise = {
            nameEx: this.state.exName,
            durationEx: this.state.exDuration,
            positionEx: this.state.exPosition,
            coachEx: this.state.exCoach
        };

        axios.post("http://localhost:8081/rest/exercise", exercise)
            .then(response => {
                if(response.data != null) {
                    this.setState({"show":true});
                    setTimeout(() => this.setState({"show":false}), 3000);
                } else {
                    this.setState({"show":false});
                }
            });

        this.setState(this.initialState);
    }

    exerciseChange=event=>{
        this.setState({
            [event.target.name]:event.target.value
        });
    };

    render(){
        const{nameEx, durationEx, positionEx, coachEx}=this.state

        return(
            <div>
                <div style={{"display":this.state.show ? "block" : "none"}}>
                    <MyToast children = {{show:this.state.show, message:"Exercise Added Successfully."}}/>
                </div>
                <Card className={"border border-dark bg-dark text-white"}>
                    <Card.Header>
                        <FontAwesomeIcon icon={faPlusSquare} /> Add Exercise
                    </Card.Header>
                    <Form onReset={this.resetExercise} onSubmit={this.submitExercise} id="exerciseFormId"> 
                        <Card.Body>
                            <Form.Row>
                                <Form.Group as={Col} controlId="formGridName">
                                    <Form.Label>Exercise Name</Form.Label>
                                    <Form.Control required autoComplete="off" type="text" name="nameEx"
                                    value={nameEx}
                                    onChange={this.exerciseChange} 
                                    className={"bg-dark text-white"} placeholder="Exercise Name" />
                                    <Form.Text className="text-muted">
                                    Enter the exercise name.
                                    </Form.Text>
                                </Form.Group>
                                <Form.Group as={Col} controlId="formGridDuration">
                                    <Form.Label>Exercise Duration</Form.Label>
                                    <Form.Control required autoComplete="off" type="text" name="durationEx" 
                                    value={durationEx}
                                    onChange={this.exerciseChange}
                                    className={"bg-dark text-white"} placeholder="Exercise Duration" />
                                    <Form.Text className="text-muted">
                                    Enter the exercise duration.
                                    </Form.Text>
                                </Form.Group>
                            </Form.Row>
                            <Form.Row>
                                <Form.Group as={Col} controlId="formGridPosition">
                                    <Form.Label>Player Position</Form.Label>
                                    <Form.Control required autoComplete="off" type="text" name="positionEx" 
                                    value={positionEx}
                                    onChange={this.exerciseChange}
                                    className={"bg-dark text-white"} placeholder="Player Position" />
                                    <Form.Text className="text-muted">
                                    Enter the player position for this exercise.
                                    </Form.Text>
                                </Form.Group>
                                <Form.Group as={Col} controlId="formGridCoach">
                                    <Form.Label>Coach Name</Form.Label>
                                    <Form.Control required autoComplete="off" type="text" name="coachEx" 
                                    value={coachEx}
                                    onChange={this.exerciseChange}
                                    className={"bg-dark text-white"} placeholder="Coach Name" />
                                    <Form.Text className="text-muted">
                                    Enter the name of the coach who will lead the exercise.
                                    </Form.Text>
                                </Form.Group>
                            </Form.Row>
                        </Card.Body>
                        <Card.Footer style={{"textAlign":"right"}}>
                            <Button variant="success" type="submit">
                                <FontAwesomeIcon icon={faSave} /> Submit
                            </Button>{' '}
                            <Button size="sm" variant="info" type="reset">
                                <FontAwesomeIcon icon={faUndo} /> Reset
                            </Button>
                        </Card.Footer>
                    </Form>
                </Card>
            </div>
        );
    }
}
