import {SAVE_EX_REQUEST, SAVE_EX_SUCCESS, SAVE_EX_FAILURE} from "./exerciseTypes";

const initialState = {
    exercise: '', error: ''
};

const reducer = (state = initialState, action) => {
    switch(action.type) {
        case SAVE_EX_REQUEST || FETCH_EX_REQUEST || UPDATE_EX_REQUEST || DELETE_EX_REQUEST:
            return {
                ...state
            };
        case EX_SUCCESS:
            return {
                exercise: action.payload,
                error: ''
            };
        case EX_FAILURE:
            return {
                exercise: '',
                error: action.payload
            };
        default: return state;
    }
};

export default reducer;
