import React, { Component } from 'react';

import {Card, Table, Image, ButtonGroup, Button} from 'react-bootstrap';
import {FontAwesomeIcon} from '@fortawesome/react-fontawesome';
import {faList, faEdit, faTrash, faCheck, faCheckSquare} from '@fortawesome/free-solid-svg-icons';
import axios from 'axios';

export default class Training extends Component {

    constructor(props) {
        super(props);
        this.state = {
            exercises : []
        };
    }

    componentDidMount() {
        this.findAllExercises();
    }

    findAllExercises() {
        axios.get("http://localhost:8081/rest/exercise")
            .then(response => response.data)
            .then((data) => {
                this.setState({exercises: data});
            });
    }

    render(){
        return(
        <Card className={"border border-dark bg-dark text-white"}>
            <Card.Header><FontAwesomeIcon icon={faList} /> Training</Card.Header>
            <Card.Body>
                <Table striped bordered hover variant="dark">
                    <thead>
                        <tr>
                        <th>Name</th>
                        <th>Duration</th>
                        <th>Position</th>
                        <th>Coach</th>
                        <th>Action</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            this.state.exercises.length === 0 ?
                            <tr align="center">
                               <td colSpan="4">No Exercises Available</td>
                            </tr> :
                            this.state.exercises.map((exercise) => (
                            <tr key={exercise.id}>
                                <td>
                                    <FontAwesomeIcon icon={faCheckSquare} /> {exercise.exName}
                                </td>
                                <td>{exercise.exDuration}</td>
                                <td>{exercise.exPosition}</td>
                                <td>{exercise.exCoach}</td>
                                <td>
                                    <ButtonGroup>
                                        <div style="text-align:center;">
                                        <Button size="sm" variant="outline-primary"><FontAwesomeIcon icon={faEdit} /></Button>{' '}
                                        <Button size="sm" variant="outline-danger"><FontAwesomeIcon icon={faTrash} /></Button>
                                        </div>
                                    </ButtonGroup>
                                </td>
                            </tr>
                            ))
                        }
                    </tbody>
                </Table>
            </Card.Body>
        </Card>
        );
    }
}
