import React, { Component } from 'react';

import {Card, Table, Image, ButtonGroup, Button} from 'react-bootstrap';
import {FontAwesomeIcon} from '@fortawesome/react-fontawesome';
import {faList, faEdit, faTrash, faCheck, faCheckSquare} from '@fortawesome/free-solid-svg-icons';
import axios from 'axios';
import MyToast from './MyToast';
import {Link} from 'react-router-dom';

export default class Training extends Component {

    constructor(props) {
        super(props);
        this.state = {
            exercises : []
        };
    }

    componentDidMount() {
        this.findAllExercises();
    }

    findAllExercises() {
        axios.get("http://localhost:8081/rest/exercise")
            .then(response => response.data)
            .then((data) => {
                this.setState({exercises: data});
            });
    };

    // findAllExercises() {
    //     fetch("http://localhost:8081/rest/exercise")
    //         .then(response => response.json)
    //         .then((data) => {
    //             this.setState({exercises: data});
    //         });
    // };

    deleteExercise = (exerciseId) => {
        axios.delete("http://localhost:8081/rest/exercise/"+exerciseId)
            .then(response => {
                if(response.data != null) {
                    this.setState({"show":true});
                    setTimeout(() => this.setState({"show":false}), 3000);
                    this.setState({
                        exercises: this.state.exercises.filter(exercise => exercise.id !== exerciseId)
                    });
                } else {
                    this.setState({"show":false});
                }
            });
    };

    render(){
        return(
        <div>
            <div style={{"display":this.state.show ? "block" : "none"}}>
            <MyToast show = {this.state.show} message = {"Exercise Deleted Successfully."} type = {"danger"}/>
            </div>
            <Card className={"border border-dark bg-dark text-white"}>
                <Card.Header><FontAwesomeIcon icon={faList} /> Training</Card.Header>
                <Card.Body>
                    <Table striped bordered hover variant="dark">
                        <thead>
                            <tr>
                            <th>Name</th>
                            <th>Duration</th>
                            <th>Position</th>
                            <th>Coach</th>
                            <th>Action</th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                this.state.exercises.length === 0 ?
                                <tr align="center">
                                <td colSpan="5">No Exercises Available</td>
                                </tr> :
                                this.state.exercises.map((exercise) => (
                                <tr key={exercise.id}>
                                    <td>
                                        <FontAwesomeIcon icon={faCheck} /> {exercise.exName}
                                    </td>
                                    <td>{exercise.exDuration}</td>
                                    <td>{exercise.exPosition}</td>
                                    <td>{exercise.exCoach}</td>
                                    <td>
                                        <ButtonGroup>
                                            <Link to={"edit/"+exercise.id} className="btn btn-sm btn-outline-primary"><FontAwesomeIcon icon={faEdit} /></Link>{' '}
                                            <Button size="sm" variant="outline-danger" onClick={this.deleteExercise.bind(this, exercise.id)}><FontAwesomeIcon icon={faTrash} /></Button>
                                        </ButtonGroup>
                                    </td>
                                </tr>
                                ))
                            }
                        </tbody>
                    </Table>
                </Card.Body>
            </Card>
        </div>
        );
    }
}
